mapper.select:
SELECT DATE_FORMAT(logDate,'%m') as userId, sum(payment) as payment FROM shop_log WHERE YEAR(logDate) = YEAR(SYSDATE())
AND productNum = #{productNum}
GROUP BY DATE_FORMAT(logDate,'%m')

SELECT DATE_FORMAT(logDate,'%m') as userId, count(payment) as payment FROM shop_log WHERE YEAR(logDate) = YEAR(SYSDATE())
AND productNum = #{productNum}
GROUP BY DATE_FORMAT(logDate,'%m')
